import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'

export default defineConfig({
  plugins: [react()]
})

*,
*::before,
*::after { box-sizing: border-box; }
body { margin: 0; font-family: system-ui, sans-serif; background: #0d1117; color: #c9d1d9; min-height: 100vh; }
#root { height: 100%; }

.nav {
  background: #161b22;
  padding: 1.5rem;
  display: flex;
  gap: 3rem;
  justify-content: center;
  position: sticky;
  top: 0;
  z-index: 10;
}
.nav a {
  color: #58a6ff;
  text-decoration: none;
  font-size: 1.1rem;
  font-weight: 500;
}
.nav a.active {
  color: white;
  border-bottom: 3px solid #58a6ff;
  padding-bottom: 0.5rem;
}
main {
  padding: 3rem 1.5rem;
  max-width: 1200px;
  margin: 0 auto;
}
h1 { color: #58a6ff; }
.grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 2rem;
  margin-top: 2rem;
}
.card {
  background: #161b22;
  padding: 1.5rem;
  border: 1px solid #30363d;
  border-radius: 8px;
}

export default function Home() {
  return (
    <div>
      <h1>Home</h1>
      <p>Welcome to the complete Vite + React + React Router project.</p>
      <p>Four fully functional client-side pages with instant navigation.</p>
    </div>
  )
}

const products = [
  { id: 1, name: "Alpha", price: 199 },
  { id: 2, name: "Beta", price: 299 },
  { id: 3, name: "Gamma", price: 399 }
]

export default function Products() {
  return (
    <div>
      <h1>Products</h1>
      <div className="grid">
        {products.map(p => (
          <div key={p.id} className="card">
            <h2>{p.name}</h2>
            <p>${p.price}</p>
          </div>
        ))}
      </div>
    </div>
  )
}
